
public class ControlFlow2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//if문
		
		int i = 5;
		if(i > 5) {
			
			System.out.println("5보다 큽니다");
			
			
		} else {
			if(i==5) {
				
				System.out.println("5랑 같습니다.");
			}
			
			if(i<5) {
				System.out.println("5보다 작습니다");
			}
			
			
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
