
public class Loop6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1;
		int sumd = 0;
		int sumf = 0;

		for (i = 1; i <= 10; i++) {

			if (i % 2 == 0) {
				sumd += i;
			}

			else {
				sumf += i;
			}
			
		}
		System.out.println(sumd);
		System.out.println(sumf);
		
		
		
		
		
		
		
		
		
		
		/*
		 * int i=1; int sum=0;
		 * 
		 * for(i=1;i<=10;i++) {sum +=i;}
		 * 
		 * System.out.println(sum);
		 */

	}

}
