public class Loop7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i;
		int j;
		int k=5;
		
		for(j=0;j<5;j++) {
			
			k--;
		
			for (i=5;i>k;i--) {
				
				System.out.print("*");
				
				
			}
			System.out.println();
			
		}
		
		
		
		}
		
		

		
		
		
		
	}


